import { useState, useEffect } from "react";
import axios from "axios";
import { Home, FileText, MessageSquare, User, Settings, ChevronDown, ChevronUp, CheckCircle, XCircle, Clock as ClockIcon, AlertCircle } from "lucide-react";

const StudentDashboard = () => {
  // State for navigation
  const [activeTab, setActiveTab] = useState("home");

  // State for dashboard data
  const [studentId, setStudentId] = useState("60d21b4667d0d8992e610c85"); // Ensure this ID is valid
  const [studentData, setStudentData] = useState({
    name: "",
    email: "",
    id: "",
    department: "",
    year: "",
    profileImage: null,
  });

  // State for queries
  const [queries, setQueries] = useState([]);
  const [selectedQuery, setSelectedQuery] = useState(null);
  const [queriesFilter, setQueriesFilter] = useState("all");

  // Fetch user data
  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const studentResponse = await axios.get(`http://localhost:5000/students/${studentId}`);
        setStudentData(studentResponse.data);
      } catch (error) {
        console.error("Error fetching user data:", error);
      }
    };

    if (studentId) {
      fetchUserData();
    }
  }, [studentId]);

  // Render dashboard content based on active tab
  const renderContent = () => {
    switch (activeTab) {
      case "home":
        return (
          <div className="p-6">
            <h2 className="text-2xl font-bold mb-6">Student Dashboard</h2>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
              {/* Profile Summary Card */}
              <div className="bg-white rounded-lg shadow p-6">
                <h3 className="text-lg font-semibold mb-2">Profile</h3>
                <div className="flex justify-between items-center">
                  <span className="text-xl font-semibold">{studentData.name}</span>
                  <User className="h-8 w-8 text-blue-500" />
                </div>
                <div className="mt-2 text-sm text-gray-600">
                  <div>{studentData.department}</div>
                  <div>{studentData.year}</div>
                </div>
              </div>
            </div>
          </div>
        );

      case "queries":
        return (
          <div className="p-6">
            <h2 className="text-2xl font-bold mb-6">My Queries</h2>

            {/* Filter tabs */}
            <div className="flex border-b px-4">
              <button
                onClick={() => setQueriesFilter("all")}
                className={`py-3 px-4 border-b-2 font-medium text-sm ${
                  queriesFilter === "all" ? "border-blue-600 text-blue-600" : "border-transparent text-gray-500 hover:text-gray-700"
                }`}
              >
                All
              </button>
              <button
                onClick={() => setQueriesFilter("Pending")}
                className={`py-3 px-4 border-b-2 font-medium text-sm ${
                  queriesFilter === "Pending" ? "border-blue-600 text-blue-600" : "border-transparent text-gray-500 hover:text-gray-700"
                }`}
              >
                Pending
              </button>
              <button
                onClick={() => setQueriesFilter("In Progress")}
                className={`py-3 px-4 border-b-2 font-medium text-sm ${
                  queriesFilter === "In Progress" ? "border-blue-600 text-blue-600" : "border-transparent text-gray-500 hover:text-gray-700"
                }`}
              >
                In Progress
              </button>
              <button
                onClick={() => setQueriesFilter("Answered")}
                className={`py-3 px-4 border-b-2 font-medium text-sm ${
                  queriesFilter === "Answered" ? "border-blue-600 text-blue-600" : "border-transparent text-gray-500 hover:text-gray-700"
                }`}
              >
                Answered
              </button>
              <button
                onClick={() => setQueriesFilter("Closed")}
                className={`py-3 px-4 border-b-2 font-medium text-sm ${
                  queriesFilter === "Closed" ? "border-blue-600 text-blue-600" : "border-transparent text-gray-500 hover:text-gray-700"
                }`}
              >
                Closed
              </button>
            </div>

            {/* Queries List */}
            <div className="bg-white rounded-lg shadow mt-4">
              {queries.length === 0 ? (
                <div className="p-8 text-center text-gray-500">No queries found.</div>
              ) : (
                <div className="divide-y">
                  {queries.map((query) => (
                    <div
                      key={query._id}
                      className={`p-4 hover:bg-gray-50 cursor-pointer ${
                        selectedQuery && selectedQuery._id === query._id ? "bg-gray-50" : ""
                      }`}
                      onClick={() => setSelectedQuery(query)}
                    >
                      <div className="flex justify-between items-start">
                        <div>
                          <h3 className="font-medium">{query.category}</h3>
                          <p className="text-sm text-gray-600 line-clamp-2">{query.description}</p>
                          <div className="flex items-center gap-2 mt-1 text-xs text-gray-500">
                            <Calendar className="h-3 w-3" />
                            <span>{new Date(query.date).toLocaleDateString()}</span>
                          </div>
                        </div>
                        <span className={`px-2 py-1 rounded-full text-xs font-semibold flex items-center gap-1 ${getStatusColor(query.status)}`}>
                          {getStatusIcon(query.status)}
                          {query.status}
                        </span>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>

            {/* Query Detail View */}
            {selectedQuery && (
              <div className="mt-6 bg-white rounded-lg shadow p-6">
                <div className="flex justify-between items-center mb-4">
                  <h3 className="text-lg font-bold">{selectedQuery.category}</h3>
                  <span className={`px-2 py-1 rounded-full text-xs font-semibold flex items-center gap-1 ${getStatusColor(selectedQuery.status)}`}>
                    {getStatusIcon(selectedQuery.status)}
                    {selectedQuery.status}
                  </span>
                </div>

                <div className="flex items-center gap-2 mb-2 text-sm text-gray-500">
                  <Calendar className="h-4 w-4" />
                  <span>Submitted: {new Date(selectedQuery.date).toLocaleDateString()}</span>
                </div>

                <div className="bg-gray-50 p-4 rounded-md mb-6">
                  <p className="whitespace-pre-wrap">{selectedQuery.description}</p>
                </div>

                {/* Conversation */}
                <div className="mb-4">
                  <h4 className="font-medium mb-2">Conversation</h4>
                  {selectedQuery.replies && selectedQuery.replies.length > 0 ? (
                    <div className="space-y-4">
                      {selectedQuery.replies.map((reply) => (
                        <div
                          key={reply._id}
                          className={`p-3 rounded-lg ${
                            reply.isAdmin ? "bg-blue-50 ml-8" : "bg-gray-50 mr-8"
                          }`}
                        >
                          <div className="flex items-center gap-2 mb-1">
                            <span className="font-medium text-sm">{reply.isAdmin ? "Admin" : "You"}</span>
                            <span className="text-xs text-gray-500">{new Date(reply.date).toLocaleString()}</span>
                          </div>
                          <p className="text-sm">{reply.content}</p>
                        </div>
                      ))}
                    </div>
                  ) : (
                    <div className="text-center py-4 text-gray-500">No replies yet.</div>
                  )}
                </div>
              </div>
            )}
          </div>
        );

      default:
        return null;
    }
  };

  // Helper functions for status
  const getStatusColor = (status) => {
    switch (status) {
      case "Pending":
        return "bg-yellow-100 text-yellow-800";
      case "In Progress":
        return "bg-blue-100 text-blue-800";
      case "Answered":
        return "bg-green-100 text-green-800";
      case "Closed":
        return "bg-gray-100 text-gray-800";
      default:
        return "bg-gray-100 text-gray-800";
    }
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case "Pending":
        return <AlertCircle className="h-4 w-4" />;
      case "In Progress":
        return <ClockIcon className="h-4 w-4" />;
      case "Answered":
        return <CheckCircle className="h-4 w-4" />;
      case "Closed":
        return <XCircle className="h-4 w-4" />;
      default:
        return <AlertCircle className="h-4 w-4" />;
    }
  };

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Navbar */}
      <div className="bg-white shadow-sm">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between h-16">
            <div className="flex items-center">
              <h1 className="text-xl font-bold text-blue-600">Student Query Portal</h1>
            </div>
            <div className="flex items-center">
              <div className="mr-4 flex items-center text-sm font-medium text-gray-700">
                <User className="h-5 w-5 mr-1 text-gray-400" />
                {studentData.name}
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Main Container */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
        <div className="flex flex-col md:flex-row gap-6">
          {/* Sidebar */}
          <div className="w-full md:w-64 shrink-0">
            <div className="bg-white rounded-lg shadow">
              <nav className="flex flex-col p-2">
                <button
                  onClick={() => setActiveTab("home")}
                  className={`flex items-center px-3 py-2 rounded-md text-sm font-medium ${
                    activeTab === "home" ? "bg-blue-100 text-blue-700" : "text-gray-700 hover:bg-gray-50"
                  }`}
                >
                  <Home className="h-5 w-5 mr-2" />
                  Dashboard
                </button>
                <button
                  onClick={() => setActiveTab("new-query")}
                  className={`flex items-center px-3 py-2 rounded-md text-sm font-medium ${
                    activeTab === "new-query" ? "bg-blue-100 text-blue-700" : "text-gray-700 hover:bg-gray-50"
                  }`}
                >
                  <FileText className="h-5 w-5 mr-2" />
                  New Query
                </button>
                <button
                  onClick={() => setActiveTab("queries")}
                  className={`flex items-center px-3 py-2 rounded-md text-sm font-medium ${
                    activeTab === "queries" ? "bg-blue-100 text-blue-700" : "text-gray-700 hover:bg-gray-50"
                  }`}
                >
                  <MessageSquare className="h-5 w-5 mr-2" />
                  My Queries
                </button>
                <button
                  onClick={() => setActiveTab("profile")}
                  className={`flex items-center px-3 py-2 rounded-md text-sm font-medium ${
                    activeTab === "profile" ? "bg-blue-100 text-blue-700" : "text-gray-700 hover:bg-gray-50"
                  }`}
                >
                  <User className="h-5 w-5 mr-2" />
                  Profile
                </button>
                <button
                  onClick={() => setActiveTab("settings")}
                  className={`flex items-center px-3 py-2 rounded-md text-sm font-medium ${
                    activeTab === "settings" ? "bg-blue-100 text-blue-700" : "text-gray-700 hover:bg-gray-50"
                  }`}
                >
                  <Settings className="h-5 w-5 mr-2" />
                  Settings
                </button>
              </nav>
            </div>
          </div>

          {/* Main Content */}
          <div className="flex-1 bg-white rounded-lg shadow">{renderContent()}</div>
        </div>
      </div>
    </div>
  );
};

export default StudentDashboard;
