import { useState, useEffect } from "react";
import axios from "axios";
import { Calendar, Clock, User, FileText, Home, Settings, LogOut, ChevronDown, ChevronUp, CheckCircle, XCircle, Clock as ClockIcon, AlertCircle, MessageSquare } from "lucide-react";

const StudentDashboard = () => {
  // State for navigation
  const [activeTab, setActiveTab] = useState("home");
  
  // State for query submission
  const [showCalendar, setShowCalendar] = useState(false);
  const [date, setDate] = useState("");
  const [category, setCategory] = useState("");
  const [description, setDescription] = useState("");
  const [isSubmitting, setIsSubmitting] = useState(false);
  
  // State for dashboard data
  const [studentId, setStudentId] = useState("60d21b4667d0d8992e610c85"); // Ensure this ID is valid
  const [studentData, setStudentData] = useState({
    name: "",
    email: "",
    id: "",
    department: "",
    year: "",
    profileImage: null
  });
  
  // State for queries
  const [queries, setQueries] = useState([]);
  const [selectedQuery, setSelectedQuery] = useState(null);
  const [queriesFilter, setQueriesFilter] = useState("all");
  const [replyContent, setReplyContent] = useState("");
  
  // Calendar utilities
  const months = [
    "January", "February", "March", "April", "May", "June", 
    "July", "August", "September", "October", "November", "December"
  ];
  
  const currentDate = new Date();
  const [currentMonth, setCurrentMonth] = useState(currentDate.getMonth());
  const [currentYear, setCurrentYear] = useState(currentDate.getFullYear());
  
  // Fetch user data and queries
  useEffect(() => {
    const fetchUserData = async () => {
      try {
        // Fetch student data
        const studentResponse = await axios.get(`http://localhost:5000/students/${studentId}`);
        setStudentData(studentResponse.data); // Update state with fetched data
        
        // Fetch student queries
        const queriesResponse = await axios.get(`http://localhost:5000/students/${studentId}/queries`);
        setQueries(queriesResponse.data || []);
      } catch (error) {
        console.error("Error fetching user data:", error);
      }
    };
    
    if (studentId) {
      fetchUserData(); // Fetch data when component mounts
    }
  }, [studentId]); // Re-run if studentId changes
  
  // Rest of the code remains the same...
  
  return (
    <div className="min-h-screen bg-gray-100">
      {/* Navbar */}
      <div className="bg-white shadow-sm">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between h-16">
            <div className="flex items-center">
              <h1 className="text-xl font-bold text-blue-600">Student Query Portal</h1>
            </div>
            
            <div className="flex items-center">
              <div className="mr-4 flex items-center text-sm font-medium text-gray-700">
                <User className="h-5 w-5 mr-1 text-gray-400" />
                {studentData.name}
              </div>
              <button className="p-1 rounded-full hover:bg-gray-100">
                <LogOut className="h-5 w-5 text-gray-400" />
              </button>
            </div>
          </div>
        </div>
      </div>
      
      {/* Main Container */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
        <div className="flex flex-col md:flex-row gap-6">
          {/* Sidebar */}
          <div className="w-full md:w-64 shrink-0">
            <div className="bg-white rounded-lg shadow">
              <nav className="flex flex-col p-2">
                <button
                  onClick={() => setActiveTab("home")}
                  className={`flex items-center px-3 py-2 rounded-md text-sm font-medium ${
                    activeTab === "home"
                      ? "bg-blue-100 text-blue-700"
                      : "text-gray-700 hover:bg-gray-50"
                  }`}
                >
                  <Home className="h-5 w-5 mr-2" />
                  Dashboard
                </button>
                
                <button
                  onClick={() => setActiveTab("new-query")}
                  className={`flex items-center px-3 py-2 rounded-md text-sm font-medium ${
                    activeTab === "new-query"
                      ? "bg-blue-100 text-blue-700"
                      : "text-gray-700 hover:bg-gray-50"
                  }`}
                >
                  <FileText className="h-5 w-5 mr-2" />
                  New Query
                </button>
                
                <button
                  onClick={() => setActiveTab("queries")}
                  className={`flex items-center px-3 py-2 rounded-md text-sm font-medium ${
                    activeTab === "queries"
                      ? "bg-blue-100 text-blue-700"
                      : "text-gray-700 hover:bg-gray-50"
                  }`}
                >
                  <MessageSquare className="h-5 w-5 mr-2" />
                  My Queries
                </button>
                
                <button
                  onClick={() => setActiveTab("profile")}
                  className={`flex items-center px-3 py-2 rounded-md text-sm font-medium ${
                    activeTab === "profile"
                      ? "bg-blue-100 text-blue-700"
                      : "text-gray-700 hover:bg-gray-50"
                  }`}
                >
                  <User className="h-5 w-5 mr-2" />
                  Profile
                </button>
                
                <button
                  onClick={() => setActiveTab("settings")}
                  className={`flex items-center px-3 py-2 rounded-md text-sm font-medium ${
                    activeTab === "settings"
                      ? "bg-blue-100 text-blue-700"
                      : "text-gray-700 hover:bg-gray-50"
                  }`}
                >
                  <Settings className="h-5 w-5 mr-2" />
                  Settings
                </button>
              </nav>
            </div>
          </div>
          
          {/* Main Content */}
          <div className="flex-1 bg-white rounded-lg shadow">
            {renderContent()}
          </div>
        </div>
      </div>
    </div>
  );
};

export default StudentDashboard;
