import { useEffect, useState } from "react";
import axios from "axios";

const StudentDashboard = ({ userId }) => {
  const [user, setUser] = useState(null);
  const [queries, setQueries] = useState([]);
  const [pendingCount, setPendingCount] = useState(0);
  const [answeredCount, setAnsweredCount] = useState(0);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchUserProfile();
    fetchUserQueries();
  }, []);

  const fetchUserProfile = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/user/${userId}`);
      setUser(response.data);
    } catch (error) {
      console.error("Error fetching user profile:", error);
    }
  };

  const fetchUserQueries = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/queries?studentId=${userId}`);
      setQueries(response.data);

      // Calculate Pending & Answered Count
      const pending = response.data.filter(q => q.status === "Pending").length;
      const answered = response.data.filter(q => q.status === "Answered").length;

      setPendingCount(pending);
      setAnsweredCount(answered);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching queries:", error);
      setLoading(false);
    }
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold text-center mb-6">Student Dashboard</h2>
      
      <div className="grid grid-cols-2 gap-6">
        {/* Queries Card */}
        <div className="bg-white shadow-md rounded-lg p-6">
          <h3 className="text-lg font-semibold">Queries</h3>
          <p className="text-3xl font-bold mt-2">{pendingCount + answeredCount}</p>
          <p className="text-sm text-gray-600">Pending: {pendingCount}</p>
          <p className="text-sm text-gray-600">Answered: {answeredCount}</p>
        </div>

        {/* Profile Card */}
        {user && (
          <div className="bg-white shadow-md rounded-lg p-6">
            <h3 className="text-lg font-semibold">Profile</h3>
            <p className="text-xl font-bold mt-2">{user.name}</p>
            <p className="text-gray-600">{user.department}</p>
          </div>
        )}
      </div>

      {/* Recent Queries */}
      <div className="mt-6">
        <h3 className="text-lg font-semibold mb-2">Recent Queries</h3>
        {loading ? (
          <p>Loading queries...</p>
        ) : queries.length === 0 ? (
          <p className="text-gray-600">No queries submitted yet.</p>
        ) : (
          <ul className="bg-white shadow-md rounded-lg p-4">
            {queries.slice(0, 3).map(query => (
              <li key={query._id} className="border-b py-2">
                <span className="font-semibold">{query.category}:</span> {query.description}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default StudentDashboard;
