import { useState, useEffect } from "react";
import axios from "axios";
import { Home, FileText, MessageSquare, User, Settings } from "lucide-react";

const StudentDashboard = () => {
  // State for navigation
  const [activeTab, setActiveTab] = useState("home");

  // State for dashboard data
  const [studentId, setStudentId] = useState("60d21b4667d0d8992e610c85"); // Ensure this ID is valid
  const [studentData, setStudentData] = useState({
    name: "",
    email: "",
    id: "",
    department: "",
    year: "",
    profileImage: null,
  });

  // Fetch user data
  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const studentResponse = await axios.get(`http://localhost:5000/students/${studentId}`);
        setStudentData(studentResponse.data);
      } catch (error) {
        console.error("Error fetching user data:", error);
      }
    };

    if (studentId) {
      fetchUserData();
    }
  }, [studentId]);

  // Render dashboard content based on active tab
  const renderContent = () => {
    switch (activeTab) {
      case "home":
        return (
          <div className="p-6">
            <h2 className="text-2xl font-bold mb-6">Student Dashboard</h2>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
              {/* Profile Summary Card */}
              <div className="bg-white rounded-lg shadow p-6">
                <h3 className="text-lg font-semibold mb-2">Profile</h3>
                <div className="flex justify-between items-center">
                  <span className="text-xl font-semibold">{studentData.name}</span>
                  <User className="h-8 w-8 text-blue-500" />
                </div>
                <div className="mt-2 text-sm text-gray-600">
                  <div>{studentData.department}</div>
                  <div>{studentData.year}</div>
                </div>
              </div>
            </div>
          </div>
        );

      default:
        return null;
    }
  };

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Navbar */}
      <div className="bg-white shadow-sm">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between h-16">
            <div className="flex items-center">
              <h1 className="text-xl font-bold text-blue-600">Student Query Portal</h1>
            </div>
            <div className="flex items-center">
              <div className="mr-4 flex items-center text-sm font-medium text-gray-700">
                <User className="h-5 w-5 mr-1 text-gray-400" />
                {studentData.name}
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Main Container */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
        <div className="flex flex-col md:flex-row gap-6">
          {/* Sidebar */}
          <div className="w-full md:w-64 shrink-0">
            <div className="bg-white rounded-lg shadow">
              <nav className="flex flex-col p-2">
                <button
                  onClick={() => setActiveTab("home")}
                  className={`flex items-center px-3 py-2 rounded-md text-sm font-medium ${
                    activeTab === "home" ? "bg-blue-100 text-blue-700" : "text-gray-700 hover:bg-gray-50"
                  }`}
                >
                  <Home className="h-5 w-5 mr-2" />
                  Dashboard
                </button>
                <button
                  onClick={() => setActiveTab("new-query")}
                  className={`flex items-center px-3 py-2 rounded-md text-sm font-medium ${
                    activeTab === "new-query" ? "bg-blue-100 text-blue-700" : "text-gray-700 hover:bg-gray-50"
                  }`}
                >
                  <FileText className="h-5 w-5 mr-2" />
                  New Query
                </button>
                <button
                  onClick={() => setActiveTab("queries")}
                  className={`flex items-center px-3 py-2 rounded-md text-sm font-medium ${
                    activeTab === "queries" ? "bg-blue-100 text-blue-700" : "text-gray-700 hover:bg-gray-50"
                  }`}
                >
                  <MessageSquare className="h-5 w-5 mr-2" />
                  My Queries
                </button>
                <button
                  onClick={() => setActiveTab("profile")}
                  className={`flex items-center px-3 py-2 rounded-md text-sm font-medium ${
                    activeTab === "profile" ? "bg-blue-100 text-blue-700" : "text-gray-700 hover:bg-gray-50"
                  }`}
                >
                  <User className="h-5 w-5 mr-2" />
                  Profile
                </button>
                <button
                  onClick={() => setActiveTab("settings")}
                  className={`flex items-center px-3 py-2 rounded-md text-sm font-medium ${
                    activeTab === "settings" ? "bg-blue-100 text-blue-700" : "text-gray-700 hover:bg-gray-50"
                  }`}
                >
                  <Settings className="h-5 w-5 mr-2" />
                  Settings
                </button>
              </nav>
            </div>
          </div>

          {/* Main Content */}
          <div className="flex-1 bg-white rounded-lg shadow">{renderContent()}</div>
        </div>
      </div>
    </div>
  );
};

export default StudentDashboard;
