import React, { useState, useEffect } from "react";
import { ChevronUp, ChevronDown, Calendar, Clock, User } from "lucide-react";

const StudentDashboard = ({ studentData, queries, notifications }) => {
  const [selectedTab, setSelectedTab] = useState("submitQuery");
  const [showCalendar, setShowCalendar] = useState(false);
  const [date, setDate] = useState("");
  const [currentMonth, setCurrentMonth] = useState(new Date().getMonth());
  const [currentYear, setCurrentYear] = useState(new Date().getFullYear());
  const [category, setCategory] = useState("");
  const [description, setDescription] = useState("");
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [queriesFilter, setQueriesFilter] = useState("all");
  const [selectedQuery, setSelectedQuery] = useState(null);
  const [replyContent, setReplyContent] = useState("");

  const months = [
    "January", "February", "March", "April", "May", "June",
    "July", "August", "September", "October", "November", "December"
  ];

  const submitQuery = async () => {
    setIsSubmitting(true);
    // Simulated API call
    setTimeout(() => {
      alert("Query submitted successfully!");
      setIsSubmitting(false);
      setDate("");
      setCategory("");
      setDescription("");
    }, 2000);
  };

  const submitReply = async () => {
    if (!replyContent.trim()) return;
    // Simulated API call
    setTimeout(() => {
      alert("Reply submitted!");
      setReplyContent("");
    }, 1500);
  };

  const filteredQueries = queries.filter((q) =>
    queriesFilter === "all" ? true : q.status === queriesFilter
  );

  const markNotificationAsRead = (id) => {
    alert(`Notification ${id} marked as read!`);
  };

  return (
    <div className="p-6">
      <div className="flex gap-4 mb-6">
        {[
          { label: "Submit Query", value: "submitQuery" },
          { label: "My Queries", value: "queries" },
          { label: "Notifications", value: "notifications" },
          { label: "Profile", value: "profile" },
        ].map(({ label, value }) => (
          <button
            key={value}
            onClick={() => setSelectedTab(value)}
            className={`px-4 py-2 font-medium rounded-md ${
              selectedTab === value ? "bg-blue-600 text-white" : "bg-gray-200"
            }`}
          >
            {label}
          </button>
        ))}
      </div>

      {selectedTab === "submitQuery" && (
        <div className="bg-white p-6 rounded-lg shadow">
          <h2 className="text-2xl font-bold mb-4">Submit a Query</h2>

          <label className="block text-sm font-medium mb-1">Select Date</label>
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            className="block w-full p-2 border rounded-md mb-4"
          />

          <label className="block text-sm font-medium mb-1">Category</label>
          <select
            value={category}
            onChange={(e) => setCategory(e.target.value)}
            className="block w-full p-2 border rounded-md mb-4"
          >
            <option value="">Select category</option>
            <option value="Academic">Academic</option>
            <option value="Financial">Financial</option>
            <option value="Administrative">Administrative</option>
            <option value="Technical">Technical</option>
            <option value="Others">Others</option>
          </select>

          <label className="block text-sm font-medium mb-1">Description</label>
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            rows="4"
            className="block w-full p-2 border rounded-md mb-4"
            placeholder="Describe your query..."
          ></textarea>

          <button
            onClick={submitQuery}
            disabled={isSubmitting || !date || !category || !description}
            className={`px-4 py-2 rounded-md text-white font-medium ${
              isSubmitting || !date || !category || !description
                ? "bg-gray-400"
                : "bg-blue-600 hover:bg-blue-700"
            }`}
          >
            {isSubmitting ? "Submitting..." : "Submit Query"}
          </button>
        </div>
      )}

      {selectedTab === "notifications" && (
        <div className="bg-white p-6 rounded-lg shadow">
          <h2 className="text-2xl font-bold mb-6">Notifications</h2>
          {notifications.length === 0 ? (
            <div className="text-center text-gray-500">No notifications.</div>
          ) : (
            notifications.map((n) => (
              <div key={n._id} className="p-4 border-b flex justify-between">
                <div>
                  <h3 className="font-medium">{n.title}</h3>
                  <p className="text-sm text-gray-600">{n.message}</p>
                </div>
                {!n.read && (
                  <button
                    onClick={() => markNotificationAsRead(n._id)}
                    className="text-blue-600 text-xs"
                  >
                    Mark as read
                  </button>
                )}
              </div>
            ))
          )}
        </div>
      )}

      {selectedTab === "profile" && (
        <div className="bg-white p-6 rounded-lg shadow max-w-2xl mx-auto">
          <h2 className="text-2xl font-bold mb-6">My Profile</h2>
          <div className="flex gap-6">
            <div>
              {studentData.profileImage ? (
                <img
                  src={studentData.profileImage}
                  alt={studentData.name}
                  className="h-24 w-24 rounded-full object-cover border-2"
                />
              ) : (
                <div className="h-24 w-24 rounded-full bg-gray-200 flex items-center justify-center">
                  <User className="h-12 w-12 text-gray-400" />
                </div>
              )}
            </div>
            <div>
              <h3 className="text-2xl font-bold">{studentData.name}</h3>
              <p className="text-gray-600">{studentData.email}</p>
              <p>Student ID: {studentData.id}</p>
              <p>Department: {studentData.department}</p>
              <p>Year: {studentData.year}</p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default StudentDashboard;
