import { useEffect, useState } from "react";
import axios from "axios";

const OfficialDashboard = () => {
  const [queries, setQueries] = useState([]);
  const [selectedQuery, setSelectedQuery] = useState(null);
  const [response, setResponse] = useState("");
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState("all");

  useEffect(() => {
    fetchQueries();
  }, []);

  const fetchQueries = () => {
    setLoading(true);
    axios.get("http://localhost:5000/queries")
      .then(response => {
        setQueries(response.data);
        setLoading(false);
      })
      .catch(error => {
        console.error("Error fetching queries:", error);
        setLoading(false);
      });
  };

  const handleStatusChange = (queryId, newStatus) => {
    axios.put(`http://localhost:5000/queries/${queryId}/status`, { status: newStatus })
      .then(() => {
        setQueries(queries.map(query => 
          query._id === queryId ? { ...query, status: newStatus } : query
        ));
        if (selectedQuery && selectedQuery._id === queryId) {
          setSelectedQuery({ ...selectedQuery, status: newStatus });
        }
      })
      .catch(error => console.error("Error updating status:", error));
  };

  const handleSubmitResponse = () => {
    if (!selectedQuery || !response.trim()) return;
    
    axios.post(`http://localhost:5000/queries/${selectedQuery._id}/response`, { 
      response: response,
      respondedBy: "Current Official" // Replace with actual official name/ID
    })
      .then(() => {
        handleStatusChange(selectedQuery._id, "Answered");
        setResponse("");
        fetchQueries(); // Refresh queries to get updated data
      })
      .catch(error => console.error("Error submitting response:", error));
  };

  const getStatusColor = (status) => {
    switch(status) {
      case "Pending": return "bg-yellow-200";
      case "In Progress": return "bg-blue-200";
      case "Answered": return "bg-green-200";
      case "Closed": return "bg-gray-200";
      default: return "bg-gray-100";
    }
  };

  const filteredQueries = filter === "all" 
    ? queries 
    : queries.filter(query => query.status === filter);

  return (
    <div className="flex flex-col h-screen">
      <header className="bg-blue-700 text-white p-4">
        <h1 className="text-2xl font-bold">Official Dashboard</h1>
      </header>
      
      <div className="flex flex-1 overflow-hidden">
        {/* Sidebar */}
        <div className="w-64 bg-gray-100 p-4 flex flex-col">
          <div className="mb-4">
            <h2 className="font-bold mb-2">Filter by Status</h2>
            <select 
              className="w-full p-2 border rounded"
              value={filter}
              onChange={(e) => setFilter(e.target.value)}
            >
              <option value="all">All Queries</option>
              <option value="Pending">Pending</option>
              <option value="In Progress">In Progress</option>
              <option value="Answered">Answered</option>
              <option value="Closed">Closed</option>
            </select>
          </div>
          
          <div className="mb-4">
            <button 
              className="w-full p-2 bg-blue-600 text-white rounded hover:bg-blue-700"
              onClick={fetchQueries}
            >
              Refresh Queries
            </button>
          </div>
          
          <div className="flex-1 overflow-y-auto">
            <h2 className="font-bold mb-2">Statistics</h2>
            <div className="bg-white p-2 rounded shadow mb-2">
              <p>Total: {queries.length}</p>
              <p>Pending: {queries.filter(q => q.status === "Pending").length}</p>
              <p>In Progress: {queries.filter(q => q.status === "In Progress").length}</p>
              <p>Answered: {queries.filter(q => q.status === "Answered").length}</p>
              <p>Closed: {queries.filter(q => q.status === "Closed").length}</p>
            </div>
          </div>
        </div>
        
        {/* Main content */}
        <div className="flex-1 flex flex-col overflow-hidden">
          {/* Queries list */}
          <div className="flex-1 overflow-y-auto p-4">
            <h2 className="text-xl font-bold mb-4">Student Queries</h2>
            
            {loading ? (
              <p>Loading queries...</p>
            ) : filteredQueries.length === 0 ? (
              <p>No queries found.</p>
            ) : (
              <div className="overflow-x-auto">
                <table className="min-w-full bg-white border">
                  <thead>
                    <tr className="bg-gray-200">
                      <th className="p-3 text-left border">Date</th>
                      <th className="p-3 text-left border">Student</th>
                      <th className="p-3 text-left border">Category</th>
                      <th className="p-3 text-left border">Description</th>
                      <th className="p-3 text-left border">Status</th>
                      <th className="p-3 text-left border">Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {filteredQueries.map((query) => (
                      <tr key={query._id} className="hover:bg-gray-50">
                        <td className="p-3 border">{new Date(query.date).toLocaleDateString()}</td>
                        <td className="p-3 border">{query.studentId.name}</td>
                        <td className="p-3 border">{query.category}</td>
                        <td className="p-3 border">{query.description.substring(0, 50)}...</td>
                        <td className={`p-3 border ${getStatusColor(query.status)}`}>
                          <span className="px-2 py-1 rounded-full text-xs font-bold">
                            {query.status}
                          </span>
                        </td>
                        <td className="p-3 border">
                          <button 
                            className="bg-blue-500 text-white px-2 py-1 rounded text-sm mr-2"
                            onClick={() => setSelectedQuery(query)}
                          >
                            View
                          </button>
                          <select 
                            className="border rounded p-1 text-sm"
                            value={query.status}
                            onChange={(e) => handleStatusChange(query._id, e.target.value)}
                          >
                            <option value="Pending">Pending</option>
                            <option value="In Progress">In Progress</option>
                            <option value="Answered">Answered</option>
                            <option value="Closed">Closed</option>
                          </select>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            )}
          </div>
          
          {/* Query details and response section */}
          {selectedQuery && (
            <div className="border-t p-4 bg-gray-50">
              <div className="flex justify-between mb-4">
                <h3 className="text-lg font-bold">Query Details</h3>
                <button 
                  className="text-gray-500"
                  onClick={() => setSelectedQuery(null)}
                >
                  Close
                </button>
              </div>
              
              <div className="grid grid-cols-2 gap-4 mb-4">
                <div>
                  <p><strong>Student:</strong> {selectedQuery.studentId.name}</p>
                  <p><strong>Date:</strong> {new Date(selectedQuery.date).toLocaleString()}</p>
                  <p><strong>Category:</strong> {selectedQuery.category}</p>
                  <p><strong>Status:</strong> 
                    <span className={`ml-2 px-2 py-1 rounded-full text-xs font-bold ${getStatusColor(selectedQuery.status)}`}>
                      {selectedQuery.status}
                    </span>
                  </p>
                </div>
                <div>
                  <p><strong>Student ID:</strong> {selectedQuery.studentId._id}</p>
                  <p><strong>Email:</strong> {selectedQuery.studentId.email || "N/A"}</p>
                  <p><strong>Phone:</strong> {selectedQuery.studentId.phone || "N/A"}</p>
                </div>
              </div>
              
              <div className="mb-4">
                <h4 className="font-bold">Query Description:</h4>
                <p className="bg-white p-3 border rounded">{selectedQuery.description}</p>
              </div>
              
              {selectedQuery.responses && selectedQuery.responses.length > 0 && (
                <div className="mb-4">
                  <h4 className="font-bold">Previous Responses:</h4>
                  {selectedQuery.responses.map((resp, index) => (
                    <div key={index} className="bg-white p-3 border rounded mb-2">
                      <p>{resp.response}</p>
                      <p className="text-sm text-gray-500 mt-1">
                        By {resp.respondedBy} on {new Date(resp.respondedAt).toLocaleString()}
                      </p>
                    </div>
                  ))}
                </div>
              )}
              
              <div>
                <h4 className="font-bold mb-2">Your Response:</h4>
                <textarea 
                  className="w-full p-2 border rounded mb-2"
                  rows="4"
                  value={response}
                  onChange={(e) => setResponse(e.target.value)}
                  placeholder="Type your response here..."
                ></textarea>
                <div className="flex justify-end">
                  <button 
                    className="bg-green-600 text-white px-4 py-2 rounded disabled:bg-gray-400"
                    disabled={!response.trim()}
                    onClick={handleSubmitResponse}
                  >
                    Submit Response
                  </button>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default OfficialDashboard;
