import { useEffect, useState } from "react";
import axios from "axios";

const OfficialDashboard = () => {
  const [queries, setQueries] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5000/queries")
      .then(response => setQueries(response.data))
      .catch(error => console.error("Error fetching queries:", error));
  }, []);

  const handleStatusChange = (id, newStatus) => {
    setQueries(prevQueries =>
      prevQueries.map(query =>
        query._id === id ? { ...query, status: newStatus } : query
      )
    );
  };

  const handleResponseChange = (id, responseText) => {
    setQueries(prevQueries =>
      prevQueries.map(query =>
        query._id === id ? { ...query, response: responseText } : query
      )
    );
  };

  const handleSaveResponse = (id) => {
    const queryToUpdate = queries.find(query => query._id === id);

    axios.put(`http://localhost:5000/queries/${id}`, {
      status: queryToUpdate.status,
      response: queryToUpdate.response,
    })
      .then(() => alert("Response saved successfully"))
      .catch(error => console.error("Error updating query:", error));
  };

  return (
    <div>
      <h2>Student Queries</h2>
      <table border="1">
        <thead>
          <tr>
            <th>Date</th>
            <th>Student</th>
            <th>Category</th>
            <th>Description</th>
            <th>Status</th>
            <th>Response</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {queries.map((query) => (
            <tr key={query._id}>
              <td>{new Date(query.date).toLocaleDateString()}</td>
              <td>{query.studentId.name}</td>
              <td>{query.category}</td>
              <td>{query.description}</td>
              <td>
                <select
                  value={query.status}
                  onChange={(e) => handleStatusChange(query._id, e.target.value)}
                >
                  <option value="Pending">Pending</option>
                  <option value="In Progress">In Progress</option>
                  <option value="Resolved">Resolved</option>
                </select>
              </td>
              <td>
                <input
                  type="text"
                  value={query.response || ""}
                  onChange={(e) => handleResponseChange(query._id, e.target.value)}
                  placeholder="Enter your response"
                />
              </td>
              <td>
                <button onClick={() => handleSaveResponse(query._id)}>Save</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default OfficialDashboard;
